const RWmodal={closer:0,init:function(){document.getElementsByTagName("body")[0].innerHTML+=`
      <div id="RWmodal" class="RWmodal">
        <div class="RWmodal-content">
          <p></p>
          <p class="RWclose" onclick="RWmodal.close();">Ok</p>
        </div>
      </div>
    `;window.onclick=function(event){if(event.target==document.getElementById('RWmodal')){if(RWmodal.closer===1){document.getElementById('RWmodal').style.display="none";}}};},open:function(mode,msg){if(arguments.length<2){alert("Missing mode arg in RWmodal.open(mode, msg)");return;}
RWmodal.closer=mode;document.querySelector(".RWmodal-content p").innerHTML=msg;if(msg.length>1800){document.querySelector(".RWmodal-content p").style="overflow: auto; height: 250px;";}
document.getElementById('RWmodal').style.display="block";},close:function(){document.getElementById('RWmodal').style.display="none";},confirm:function(routine,msg){msg+=`
      <br><br>
      <button onclick="${routine}">&nbsp;&nbsp;&nbsp;Confirm&nbsp;&nbsp;&nbsp;</button>
    `;RWmodal.open(0,msg);},prompt:function(routine,msg){msg+=`
      <br><br>
      <input type="text" id="RWid" size="32"><br><br>
      <button onclick="${routine}">&nbsp;&nbsp;&nbsp; OK &nbsp;&nbsp;&nbsp;</button>
    `;RWmodal.open(0,msg);}};document.addEventListener("DOMContentLoaded",function(event){RWmodal.init();});